INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(5,'仰卧核心卷腹','center crunch','腹部','躺在地板上，背部保持平坦紧贴地板，双腿并拢膝盖弯曲脚掌着地大腿小腿夹角为60度左右。双臂合拢放于两腿之间。','集中注意力在腹部，保持你的腹部紧张用你的上腹部和有力的朝着你的肚脐紧缩起来，同时把背部部抬离地面约十厘米。保持腹部紧缩为1秒慢慢回到起始位置。\r\n重复交替完成你的练习量','通过手持一个哑铃片在你的胸部增加训练强度',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(6,'仰卧对角交替收膝','Crossover Combo','腹部、腹内外斜肌','平躺在垫子上、把你的背部和膝盖弯曲约60度。脚心平放在地板上，把双手放于你的头后、放松','把你的右手肘和肩膀用整个身体蜷缩起来，同时把左膝朝你的左肩抬起，用右手肘接近你的左膝，触摸你的膝盖呼气：尽量尝试把你的肩膀拉向膝盖，而不仅仅是你的胳膊肘。记住，关键是当你进行运动，是收缩腹肌，不只是移动肘部。\r\n现在回到下降到起始位置时吸气，重复用左肘和右膝。\r\n继续以这种方式交替，直到所有规定的重复次数完成。','你也可以做所有你重复的一面，然后切换到另一边。',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(7,'仰卧卷腹','crunch','腹部','背部保持平坦躺在垫子上，大腿小腿之间呈60度角弯曲于地板上。让你的脚平放在地板上，把双手放松放于头部两侧。','向前卷曲你的上半身，把你的肩胛骨卷起刚刚离开地面。在任何时候，都要保持你的手臂与头部，颈部和肩部对齐。不要发生位置移动。当你进行运动的这一部分时呼气并按住收缩一秒钟。慢慢地低了下去为你吸入的起始位置。\r\n继续以这种方式交替，直到所有规定的重复次数完成','手中可添加重物。增加训练强度',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(11,'仰卧屈膝举腿转腰','Hips Drop','腹部 腰部','仰卧，双腿屈膝上提至小腿与垫面平行，大腿与垫面垂直，两臂放在体侧，手心贴在垫面上。','双腿一起向右摆，身体向右拧转至右膝触垫面，两手臂尽量不离开垫面，然后还原。双腿再向左摆，身体向左拧转至左膝触垫面。左右交替进行，重复练习。','呼吸要自然，两臂和肩部不能离开垫面，身体拧转要充分，意念要集中在腰腹部肌群。不能依靠腿的摆动。',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(15,'仰卧屈膝举腿','bent knee leg raises','腰腹部、臀部、大腿、髋部屈肌','平躺在地。双手放在腹部，或将双掌平放在大腿两侧','抬起双脚。可以让膝盖弯曲九十度，或者将两腿在空中尽力伸直。用你的腹部肌肉抬起臀部。请注意不要用你的手、背或头部向下推。若你还不能只用腹肌就抬起臀部，你还需要做更多的基本式屈膝仰卧起以增加你的腹部力量。进行膝盖弯曲腹部和臀部提升运动非常缓慢，让您真正感受到在你的腹肌紧张，所以一定要避免快速和生涩的动作','理想的范围内重复这个到你的极限，每组在15-30之间最好。每组之间休息时间60-90秒，以备你下一组锻炼之前完全恢复。',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(18,'反向卷腹','reverse curls','腹部','平躺在地板或垫子上，膝弯曲，手放置头后面，抬腿屈膝成90度，下巴与胸之间保持一定空间（眼睛斜看天花板）','呼气：朝胸部方向抬膝，保持2秒\r\n吸气：缓慢地回到起始位','不要使用惯性摆腿，尽量使用腹肌力量控制动作',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(20,'仰卧屈膝卷腹转体','Tabletop Crunch+Twist','腹部','躺在地板上，背部保持平坦肩部稍微离开地面，双腿并拢膝盖弯曲小腿和地面平行，大腿和地面垂直。双手伸直并拢放于脑后。','保持你的下腹部紧张不要拉动你的颈部，集中注意力在腹部，用你的上腹部和有力的朝着你的肚脐紧缩起来，把躯干抬离地面。\r\n保持挤压腹部为1秒慢慢回到起始位置。双手保持稳定和头部同一平面，避免摆动。注意肩部不要着地。\r\n重复完成你的练习量','通过放置一个哑铃片在你的胸部增加训练强度',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(23,'侧卧举腿卷腹(右)','Side Leg Lifts(R)','腹部、腹内外斜肌、腹肌','侧身躺于垫子或地面上，身体呈一条直线。地面的一只手臂打直伸张开来。另一只手放于腹部、保持双腿并拢。','吸气，收紧腹部、把注意力全部集中在腰腹、利用腰腹力量侧举双腿同时侧抬上身，让你的肘部接近胯部。停留一秒，返回起始位置、呼气。\r\n重复交替动作、完成规定组数','虽然活动区域很短，但你可以感到侧腹有很强的收缩掌握好平衡很关键、注意动作规范、颈部放松。不要借助惯性力量',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(24,'仰卧拉伸卷腹','stretch crunch','腹部、腿部韧带','身体呈仰卧姿、两膝关节往外拉开、脚掌心相对。然后身体仰卧于地垫上，驱赶保持平坦、双臂交叉合掌放于下腹部','呼气、收腹。用腹肌把身体向上卷起，把身体卷起离地5—10厘米，应收紧腹部肌肉并稍作停顿，然后慢慢把身体下降回原位，呼气。当背部着地的时候，便可以开始下一个循环的动作。
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(27,'仰卧起坐','Sit Ups','腹部','身体仰卧于地垫上，膝部屈曲成90度左右，脚部平放在地上，切勿把脚部固定双手安放的位置靠近头部','呼气、收腹。用腹肌把身体向上拉起，这样可确保处于腹部较深层的肌肉都同时参与工作。把身体升起离地10至20 厘米后，应收紧腹部肌肉并稍作停顿，形成坐姿。然后慢慢把身体下降回原位。当背部着地的时候，便可以开始下一个循环的动作','进行时宜采用较缓慢的速度，就如慢动作回放一般。可以有效增加腹肌韧性',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(32,'仰卧交替脚跟接触','alternate heel touchers','腹部','脚与膝盖弯曲在地板上，在地板上的脚周围18-24英寸（约0.4-0.6米）远。你的手臂在你身边两侧延长','紧缩你的躯干向前和向上约3-4英寸到右侧和触摸你的右脚后跟，你持有的收缩一秒钟。呼气，同时执行此动作。现在回去慢慢到起始位置时吸气。接着，紧缩你的躯干向前和向上约3-4英寸到左侧，触摸你的左脚后跟，你持有的收缩一秒钟。呼气，同时执行此动作，然后回到起始位置时吸气。然后，两个脚后跟都接触，重复20组。\r\n继续两侧交替进行，直到所有规定的重复次数完成。','',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(35,'平板支撑动作','elbow plank','腹部','面对地面,两只前臂向下弯曲贴紧地面，此时你的手肘和手指都是平放在地面上的。你的手掌应该收紧，并放在肩膀正下方。','你的脚趾可以微微弯曲，收紧腹部，使得腹部和肚脐处感觉向脊柱拉伸。伸直你的身体，但保持脖子和脊椎放松。想象你成为一片木板，这样你就会像箭一样直了。收紧你的腹部和臀部，直到你感觉支撑不住为止（或是感觉到身体微微出汗为止）。保持你的双眼看着你身前的地面，注意不要抬起背后。你的身体要一直保持从头到脚都在一直线上。','可以适当添加负重',1,now());
