INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(33,'仰卧抬臀','Bridge Lifts','腰腹部','背部平躺在地板上，双手平放于身体两侧。把脚掌放在地面上与肩同宽。大腿小腿的夹角约90度','把注意力集中在腰腹、用你的脚跟抬起你的臀部离开地面，同时保持背部挺直。呼气，到达顶部保持在一秒钟停顿。\r\n慢慢地回到起始的位置，吸气、重复以上动作完成目标数目','你可以用单腿支撑双腿交替',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(41,'坐姿转体扭腰','Core Twist','腰腹部','把你的膝盖弯曲，把你的两只手臂在胸前伸直，双脚平坐在地板上，背部挺直和地板面成75度夹角。','腹部紧缩拉动你的身体、扭你的躯干，双臂伸直随着腰腹的转动改变方向。然后往一边扭转，回来到中间部位停顿一秒，然后再往另一边扭转身体。\r\n这是一个完整动作、重复交替完成目标数目','可以手持哑铃。砝码、杠铃片等重物来增加强度',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(17,'空中蹬车','cross-body crunch','腹直肌、腹外斜肌、腹部深层的腹横肌','身体平躺在垫子上，双腿伸直，双臂屈肘，双手半卧拳分别放到耳侧','深吸气，使得腹部卷起到最高点，然后用一侧的肘关节去努力触碰到身体另一侧屈腿的膝盖，同时双腿中另一侧的腿离地，伸直，然后腹部始终处在收紧的状态，摆动身体，使另一侧的肘关节去触碰另一侧的膝盖，这时开始屈腿的一侧还原到伸直的状态，但要保持离地，在转体的过程中吸气，肘关节触碰到膝盖的一刹那呼吸，然后反复进行练习。','首先要注意的就是呼吸的问题。开始的时候由于腹肌力量差，动作缓慢，呼吸频率比较容易掌握。随着腹肌的增强，运动频率加快，呼吸也要随之加快。要始终保持呼吸与动作一致。',1,now());
