INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(14,'触膝卷体','Knee Touch','腹部','身体平躺在地板上。保持躯干挺直、膝盖弯曲，保持大腿小腿的夹角约为120度。双手放于脑后','吸气、注意力集中在腹部、利用腹部力量把身体卷起、同时下半身也向上卷起，用肘部去接触膝盖、接触的这个点在身体正中央、不要偏离或扭曲。\r\n到达这个时候稍微停顿两秒、然后返回起始动作，呼气','双手放松、颈部放松。力量集中在腹部，其他部位放松',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(28,'仰卧抬腿','Leg raises','腹部','臂放在身体两侧，两只手掌放于脑后，保持背部平坦仰面躺下，头部压住手掌手臂展开平放在地板上。整个身体成一条直线','吸气，把注意力集中在腹部、上半身保持静止、用腹部的力量拔腿举起，抬起双腿离开地面约60度，不要抬到和地面垂直。同时让您的双腿伸直，停留一秒钟，下去的时候注意脚跟不要触到地面.返回到起始位置，呼气。','动作尽量缓慢、腿不能弯曲。上半身保持放松静止、不能借力',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(16,'登山','mountain climbers','腹部','假设你在登山，俯卧撑的起始姿势双手撑地、身体完全伸直。用手和脚趾支撑你的身体重量，身体应形成一条直线从头部到你的脚踝。弯曲的膝盖和臀部，抬起你的右脚离地，把一条腿提到胸前左右，慢慢移动你的右膝盖朝向你的胸部。另一条腿保持伸直不动。','爆发性的交替你的腿的位置 ，通过扩展腿部弯曲背部，并把另一只脚与髋关节和膝关节屈曲，直到腿伸直的脚尖支撑。\r\n回到起始位置，重复你的左腿。重复以交替的方式20-30秒。\r\n持续交替来回的时间。完成规定练习时间','',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(53,'侧卧腹斜肌卷腹(左)','oblique crunch(L)','腹斜肌','刚开始时是以一个扭曲的姿势躺在地板上，背部尽量保持平坦，下半身扭曲把一条腿弯曲交叉于另一条腿上，一只手放于脑后。另一只手扶住上面弯曲的腿','一旦到位，注意了集中在腹部，吸气，利用腹肌力量开始紧缩把背部，头部抬起离开地面。保持抬起你的头，在任何时候都天花板你的脸平行。当你紧缩卷腹时确保你的肩胛骨没离开地面。慢慢回到起始位置然后到达位置停顿一秒、呼气','用指尖扶住你的后脑勺，以确保你的手臂不帮助紧缩，专注于完美的执行，减慢速度。也可以双腿弯曲往侧边扭曲',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(31,'侧卧腹斜肌卷腹(右)','oblique crunch(R)','腹斜肌','刚开始时是以一个扭曲的姿势躺在地板上，背部尽量保持平坦，下半身扭曲把一条腿弯曲交叉于另一条腿上，一只手放于脑后。另一只手扶住上面弯曲的腿','一旦到位，注意了集中在腹部，吸气，利用腹肌力量开始紧缩把背部，头部抬起离开地面。保持抬起你的头，在任何时候都天花板你的脸平行。当你紧缩卷腹时确保你的肩胛骨没离开地面。慢慢回到起始位置然后到达位置停顿一秒、呼气','用指尖扶住你的后脑勺，以确保你的手臂不帮助紧缩，专注于完美的执行，减慢速度。也可以双腿弯曲往侧边扭曲',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(36,'平板支撑抬腿','plank and leg lift','腹部、核心','对地面,两只前臂向下弯曲贴紧地面，此时你的手肘和手指都是平放在地面上的。你的手掌应该收紧，并放在肩膀正下方。你的脚趾可以微微弯曲，收紧腹部，使得腹部和肚脐处感觉向脊柱拉伸。伸直你的身体，但保持脖子和脊椎放松。想象你成为一片木板，并保持你的身体笔直，从头部到脚后跟。','现在把你的一只腿提高离开地面十厘米左右，腿部保持直立并保持该位置为1秒。回到起始位置，继续两腿交替。','收紧你的腹部，臀部不能往下掉。保持身体垂直',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(19,'俄罗斯转体','russian twist','腰腹部、腹斜肌','躺在地板上把你小腿，双脚交叉起来，背部保持平坦。你的腿应该膝盖微微弯曲。双脚扭在一起可以保持稳定性。保持下半身不动提升你的上身，这样躯干会创建与你的大腿一个虚构的V形。你的双臂应该完全伸展在你面前垂直于你的身体，并把双手紧握。','把注意力集中在腰腹吗，同时腰腹收缩扭动你的身体右侧，直到你的手臂与地面平行，同时呼气。\r\n保持收缩的第二个移动回到起始位置，同时呼气。然后移动到另一侧进行你右侧的相同动作。','保持下半身稳定不动。当你有足够的腰腹力量时可以较重负荷。你可以拿着一个加大的重量。比如哑铃。杠铃片、砝码，你可以借助外力来帮助固定下半身使其稳定',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(45,'仰卧交替抬腿','cissor kick','腹部','仰面躺下，你的背部紧贴地面或在运动垫下。双臂完全延伸到两侧，手掌朝下。注意：双臂整个练习过程是静止的，膝盖轻微弯曲，抬起你的腿，让你的脚后离开地面约15厘米。','抬起你的左腿高达约45度角，而你的右腿被降低，直到脚后跟离地面约5—10厘米。收紧腹部，上半身放松。通过腹部力量提高你的右腿，并降低你的左腿，切换动作。然后交替进行，记住在执行这个练习呼吸。','当你练习到一定时候可以加大负重、可以在小腿绑沙袋等类似负重',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(3,'仰卧交替摆腿','scissor legs','腹部','仰面躺下，你的背部紧贴地面或在运动垫下。双臂完全延伸到两侧，手掌朝下，把你小腿，双脚像剪刀一样交叉起来，整个腿部抬离地面10厘米悬空、注意：双臂整个练习过程是静止的','收紧腹部，上半身放松通过腹部力量双腿悬空。两脚交替横向摆动。双腿像剪刀一样水平运动，然后交替进行，记住在执行这个练习呼吸。','当你练习到一定时候可以加大负重、可以在小腿绑沙袋等类似负重',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(47,'侧桥、侧向平板支撑(左)','side bridge(L)','腹部 腹斜肌','侧身、身体保持笔直拉直你的腹肌，用你的手肘小臂支撑地面把身体撑起，身体和地面成15度夹角。另一只手臂叉腰位于侧腹部。保持稳定','把核心肌肉收紧、脊柱挺直，注意力集中在侧腹部。开始侧边卷缩你的腹斜肌，快要触到地面的时候稍稍停顿，然后返回起始位置','左右两侧交替',1,now());
