INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(29,'仰卧直腿触足卷腹','straight leg toe touches','腹部','身体仰卧在地板上。背部保持平坦贴于地面、双腿并拢抬起和地面保持垂直（和躯干成90度夹角）尽量保持双腿笔直、或微微弯曲、双手合并伸直位于胸前','收紧腹部，集中注意力在腹部，用腹部的力量抬起身体离开地面，用伸直的手尖去触摸脚尖。然后紧缩腹部停顿一秒，返回起始位置呼吸。肩部尽量不要着地','你可以手持重物。增加训练强度',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(10,'仰卧屈膝卷腹','tabletop crunch','腹部','躺在地板上，背部保持平坦肩部稍微离开地面，双腿并拢膝盖弯曲小腿和地面平行，大腿和地面垂直。双手伸直并拢放于脑后','保持你的下腹部紧张不要拉动你的颈部，集中注意力在腹部，用你的上腹部和有力的朝着你的肚脐紧缩起来，把躯干抬离地面。  保持挤压腹部为1秒慢慢回到起始位置。双手保持稳定和头部同一平面，避免摆动。注意肩部不要着地。','通过放置一个哑铃片在你的胸部增加训练强度',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(21,' 仰卧紧缩卷腹','tuck crunch','腹部','躺在地板上，背部保持平坦紧贴地板，双腿并拢伸直膝盖微微弯曲。双臂弯曲扶住头部。身体呈一条直线','集中注意力在腹部，保持你的腹部紧张用你的上腹部和有力的朝着你的肚脐紧缩起来，同时把背部和腿部抬离地面约十厘米。保持腹部紧缩为1秒慢慢回到起始位置。','通过放置一个哑铃片在你的胸部增加训练强度',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(1,'仰卧分腿两头起','adductor crunch','腹部','把身体平躺在地板上（或运动垫）保持身体伸直。背部平坦，双臂伸直分开平行放回你的头后面或者垂直于胸前，双腿伸直分开与肩同宽。','收紧腹部，把注意力集中在腹部，用腹部的力量同时抬起你的腿部和背部离开地面，把身体像折叠刀一样折起来、双腿双手保持直立分开、双腿应扩大和提升约从地板约45度角，手臂应延长并尽量触摸你的脚尖。同时吸呼吸，降低你的胳膊和腿回到起始位置。','你可以双脚使用一个健身球，以增加训练强度。',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(2,'甲壳虫仰卧交叉转体','beetle','腹部','身体平躺在垫子上，膝盖弯曲大腿小腿成90度夹角，双臂打开伸直','腹部收紧把肩膀和双腿抬离地面。扭转腹部。然后用一侧的手掌关节去努力触碰到身体另一侧屈腿的脚踝，同时双腿中另一侧的腿离地，伸直，然后腹部始终处在收紧的状态，转动腰腹，使另一侧的手掌去触碰另一侧的踝关节，这时开始屈腿的一侧还原到伸直的状态，但要保持离地，在转体的过程中吸气手掌触碰到踝关节的一刹那呼吸，然后反复进行练习。','首先要注意的就是呼吸的问题。开始的时候由于腹肌力量差，动作缓慢，呼吸频率比较容易掌握。随着腹肌的增强，运动频率加快，呼吸也要随之加快。要始终保持呼吸与动作一致。\r\n其次要注意的就是手掌要努力触碰到脚踝，只有这样才会保证运动幅度，使腹肌全部被调动起来，达到全面锻炼腹肌的目的。',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(30,'仰卧屈膝提髋','Bent Knee Hip Raises','下腹部','平躺在地板上 两侧手臂放于身体两旁，现在，把膝盖稍稍弯曲，然后把腿抬离地面和地面保持垂直','使用你的下腹部，把你的膝盖朝向你，你保持你的腿稍微弯曲。持续这个动作用下腹力量作用你的骨盆抬起臀部离开地面向上举。慢慢向上抬起臀部离地面约十厘米、进行运动的到这一部分后停留一秒，然后回到起始位置，呼气。','挤压你的腹部在运动的顶部为一秒钟，然后恢复到起始位置慢慢地你的呼吸。你可以腿部弯曲容易一些、可以伸直双腿使其更难',1,now());
