INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(8,' 俯卧海豚游泳式','dolphin','腰腹部','整个身体趴在地板上，保持身体伸直收紧腹部。双手打开伸直','把注意力集中在腰腹部，同时微微抬起你的双腿和上身。双脚和胸部不要着地同时保持背部挺直，使其整个身体保持一个微微向上的弧度。开始运动你的双臂，像蛙泳一样手臂从头部上方到大腿侧方画弧线。','你可以在小腿上绑上沙袋，手持砝码。哑铃。增加训练强度',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(50,'俯卧侧身提膝卷腹(左)','double knee lifts(L)','腹部','这个动作开始时是一个身体扭曲的动作、上半身身体俯卧，双手撑地与肩同宽。把双腿并拢侧立搭在地板上并直立。保持腰腹部扭曲。','开始收紧腹部注意力集中，用腹部力量拉动侧立的双腿做屈膝运动完成卷腹，膝盖到达下腹时保持停顿一秒，然后顺着滑行轨迹返回起始位置。','应注意不要利用腿部力量，完全靠腹部。尽量紧缩',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(37,'俯卧侧身提膝卷腹(右)','double knee lifts(R)','腹部','这个动作开始时是一个身体扭曲的动作、上半身身体俯卧，双手撑地与肩同宽。把双腿并拢侧立搭在地板上并直立。保持腰腹部扭曲。','开始收紧腹部注意力集中，用腹部力量拉动侧立的双腿做屈膝运动完成卷腹，膝盖到达下腹时保持停顿一秒，然后顺着滑行轨迹返回起始位置。','应注意不要利用腿部力量，完全靠腹部。尽量紧缩',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(12,'仰卧直腿两头起','jack knife sit up','腹部','把身体平躺在地板上（或运动垫）保持身体伸直。背部平坦，双臂伸直放回你的头后面或者垂直于胸前，双腿伸直并拢。这将是你的起始位置。','收紧腹部，把注意力集中在腹部，用腹部的力量同时抬起你的腿部和背部离开地面，把身体像折叠刀一样折起来、双腿保持直立。双腿应扩大和提升约从地板约45度角，手臂应延长并尽量触摸你的小腿。同时吸呼吸，降低你的胳膊和腿回到起始位置。','你可以双手使用一个实心球，以增加训练强度。',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(38,'平板交叉肘触膝卷腹','plank knee to elbow crunch','腹部','面对地面,两只前臂向下弯曲贴紧支撑地面，手掌应该收紧，并放在肩膀正下方。你的脚趾可以微微弯曲，收紧腹部，使得腹部和肚脐处感觉向脊柱拉伸。伸直你的身体，但保持脖子和脊椎放松。想象你成为一片木板。','收紧腹部保持背部挺直。然后提起左膝同时右肘下收努力使其膝关节和肘关节接触，同时双腿中另一侧的腿保持伸直，腹部始终处在收紧的状态，，使另一侧的肘关节去触碰另一侧的膝盖，屈腿的一侧还原到伸直的状态，在紧缩的过程中吸气，肘关节触碰到膝盖的一刹那呼吸，然后交叉反复进行练','背部始终保持挺直，不要弯腰',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(39,'平板交替伸手抬腿','plank and arm with leg raise','腹部','面对地面,两只前臂向下弯曲贴紧支撑地面，手掌应该收紧，并放在肩膀正下方。你的脚趾可以微微弯曲，收紧腹部，使得腹部和肚脐处感觉向脊柱拉伸。伸直你的身体，但保持脖子和脊椎放松。想象你成为一片木板。','收紧腹部保持背部挺直。然后同时抬起你的右腿，左手向前伸展，完成后保持停顿一秒，双腿中另一侧的腿保持伸直，腹部始终处在收紧的状态，再抬起你的左腿同时向前伸展你的右手，然后交叉反复进行练','背部始终保持挺直，不要弯腰，臀部不要下掉，可以增加重物提高训练强度，小腿帮上沙袋',1,now());
INSERT INTO tm_video_desc(`VIDEO_ID`,`CHN_NAME`,`ENG_NAME`,`TRAIN_MUSCLE`,`START_ACTION`,`ESSEN_EXERCISE`,`REMIND`,`CREATE_BY`,`CREATE_ON`) values(9,'侧平板支撑手抬高转体','push up to side plank','腹部','俯卧撑姿势。面对地面,两只前臂放在肩膀正下方向下直立支撑地面。伸直双腿，你的脚趾可以微微弯曲，收紧腹部，使得腹部和肚脐处感觉向脊柱拉伸。伸直你的身体，使你的身体和地面成一个三角形。但保持脖子和脊椎放松。','收紧腹部保持背部挺直。然后抬起你的右手伸直，身体跟着右手向后转动画弧线90度直到你的身体侧身。另一只手臂伸直支撑地面，然后换手做同样动作。交叉反复进行练习','背部始终保持挺直，不要弯腰，臀部不要下掉',1,now());
