mapper.select:
select distinct debate.*, vote.value 
from tt_debate debate
left join tt_group_user gu on gu.GROUP_ID=debate.GROUP_ID
left join tt_vote vote on vote.DEBATE_ID=debate.DEBATE_ID and vote.USER_ID=#{userId,jdbcType=INTEGER}
where debate.DEBATE_ID=#{debateId, jdbcType=INTEGER}
 and (debate.CREATE_BY=#{userId, jdbcType=INTEGER}
      or gu.OPEN_ID=(select OPEN_ID from tm_user where USER_ID=#{userId, jdbcType=INTEGER}))
