mapper.select:
select subject.*, vote.VALUE
from tt_vote_subject subject
left join tt_group_user gu on gu.GROUP_ID=subject.GROUP_ID
left join tt_vote vote on vote.SUBJECT_ID = subject.SUBJECT_ID and vote.USER_ID=#{userId, jdbcType=INTEGER}
where subject.SUBJECT_ID=#{subjectId,jdbcType=INTEGER}
 and (subject.CREATE_BY=#{userId,jdbcType=INTEGER} 
      or gu.OPEN_ID=(select OPEN_ID from tm_user where USER_ID=#{userId,jdbcType=INTEGER}))

mapper.update:
update tt_vote_subject set STATUS=0
where CREATE_BY=#{userId, jdbcType=INTEGER} 
 and SUBJECT_ID = #{subjectId,jdbcType=INTEGER}
